{{var "found"0}}
{{#if (or (not type) (eval type "==" ""))}}
  <div style="margin-top: 6px;">◾&nbsp;Missing "type" value</div>
{{/if}}
{{#if (eval type "==" "feature")}}
  {{#if (and (eval (hasOutlinks "fulfil") "==" false) (eval (hasOutlinks "fulfil_opt") "==" false))}}
    <div style="margin-top: 6px;">◾&nbsp;Missing a "fulfill" link</div>
  {{/if}}
  {{#if (and (eval (hasInlinks "refine") "==" false) (eval (hasInlinks "refine_opt") "==" false))}}
    <div style="margin-top: 6px;">◾&nbsp;Missing a "refine" link</div>
  {{/if}}
{{/if}}
{{#if (eval type "==" "d_req")}}
