    <div style="margin-top: 6px;">◾&nbsp;Missing a "refine" link</div>
  {{else}}
    {{set "found" 0}}
    {{#eachOutlinkWith type="refine"}}
      {{#if (or (eval docId "==" "26048_1") (or (eval docId "==" "Conditionals") (eval docId "==" "OptionGroups")))}}
        {{set "found" 1}}
      {{/if}}
    {{/eachOutlinkWith}}
    {{#eachOutlinkWith type="refine_opt"}}
      {{#if (or (eval docId "==" "26048_1") (or (eval docId "==" "Conditionals") (eval docId "==" "OptionGroups")))}}
        {{set "found" 1}}
      {{/if}}
    {{/eachOutlinkWith}}
    {{#if (eval (get "found") "==" 0)}}
      <div style="margin-top: 6px;">◾&nbsp;Missing a "refine" link for ISO 26048-1</div>      
    {{/if}}
  {{/if}}
  {{#if (eval (hasInlinks "implement") "==" false)}}
